import pandas as pdclass TextData():    def __init__(self, dir):        '''        Creates an instance of Textdata by reading in from dir and removes empty        lines.                ----------        dir : str            directory of the file.        Returns        -------        None.        '''                with open(dir) as text_file:            self.file = list(line for line in (l.strip() for l in text_file) if line)                                def split(self, label, start_line, end_line, num_of_splits, delim = ' '):        '''        A class method to split a text data into chunks of text.        Parameters        ----------        label : str            Label for the data.        start_line : int            Line in the text where actual text started.        end_line : int            Line in the text where actual text ended.        num_of_splits : int            Number of splits on the text.        delim : TYPE, optional            Delimiter when combining text. The default is ' '.        Returns        -------        None.        '''                temp = self.file[start_line:end_line]                temp = [temp[i:i + int(len(temp)/num_of_splits)] for i in range(0, len(temp), int(len(temp)/num_of_splits))]                temp = [delim.join(x) for x in temp]                temp = pd.DataFrame(temp)                temp['y'] = label                temp.rename(columns={0:'X'}, inplace=True)                self.data = temp                class Preprocess():    def __init__(self, text, output = False, output_dir = 'clean_data.csv'):        '''        Creates an instance of Preprocess.        Parameters        ----------        text : lst            A list of TextData object to be included in the learning.        output : boolean, optional            A boolean to control output csv. The default is False.        output_dir : str, optional            Dirctory for ouput. The default is 'clean_data.csv'.        Returns        -------        None.        '''                train_test = text[0].data                for i in range(len(text)-1):            train_test = pd.concat([train_test, text[i+1].data], axis=0)                    if output:            train_test.to_csv(output_dir)                    self.train_test = train_test                    if __name__ == '__main__':    gog = TextData('Data/The Game of Go-Arthur Smith.txt')    gog.split('gog', 25, 5542, 100)            pot = TextData('Data/A Point of Testimony-Carolyn Wells.txt')    pot.split('pot', 15, 597, 100)        pom= TextData('Data/The Passing of Morocco-Frederick F. Moore.txt')    pom.split('pom', 33, 2741, 100)        clean_data = Preprocess([gog, pot, pom], True)